mapper.select:
SELECT * FROM
(
    SELECT `videohistory`.*,@rownum := @rownum+1 AS rownum
    FROM `videohistory`,(SELECT @rownum:=0) r
    WHERE  `uID`=#{uid}
    ORDER BY `CloseTime` DESC
) AS videohistoryorder
WHERE `rownum`>=(
    SELECT `rownum`
    FROM (
        SELECT *,@rownum := @rownum+1 AS rownum
        FROM `videohistory`,(SELECT @rownum:=0) r
        WHERE `uID` = #{uid}
        ORDER BY `CloseTime` DESC
    ) AS `userdynamicrow`

        WHERE `CloseTime` < #{view_at}

    LIMIT 1
)

    AND `CloseTime` < #{max}

LIMIT #{ps}
